import './takephoto.css';
import web3 from './web3';

var Personal = require('web3-eth-personal');

class TakeRegisterPhoto extends Component {
    constructor(props) {
        super(props);
        this.state = {
            password: this.props.location.password,
            username:this.props.location.username,
            fname: this.props.location.fname,
            lname: this.props.location.lname,
            dob: this.props.location.dob,
