import React, { Component } from "react";
import axios from "axios";
import UserStorage from './stores/UserStorage';
import VoterContract from './VoterContract';
import swal from "sweetalert2";
import Candidate from "./Candidate";
import Loader from "react-loader-spinner";
import web3 from './web3';
import { Link, Redirect } from 'react-router-dom';
import './App.css';

const style = {
  width: "30%",
  marginLeft: "0%",
  height: "50px",
  marginTop: "40px",
  marginBottom: "30px",
  fontSize: "20px",
  border: "none",
  borderRight: "solid #2eca6a 3px",
  borderBottom: "solid #2eca6a 3px",
  borderTop: "solid 3px lightgrey",
  borderLeft: "solid 3px lightgrey",
};

class Welcome extends Component {
    constructor(props){
        super(props);
        this.state={
            candidates: [],
            candidate_id:0,
            max_id:'',
            loading: true,
            success: null,
            username : '',
            password : '',
            buttonDisabled : false
        }
        this.handleCandidateChange = this.handleCandidateChange.bind(this);
        this.vote= this.vote.bind(this);
      }


    doLogout(){
        UserStorage.username='';
        this.props.history.push("/Login");
    }
    async componentDidMount(){

      const response = await fetch('http://localhost:3003/candidateCount', {
          method: 'get',
          headers: {
            headers: {
              'Accept': 'application/json',
              'Content-Type': 'application/json'
            }
          }
        });

            let result = await response.json();
            console.log("response data is", result.data[0]);
            var count = result.data[0].count;
            console.log("count is: "+count);
            var accounts = await web3.eth.getAccounts();
            console.log("web3", accounts);
            console.log("aing: "+ accounts[0]);
            web3.eth.defaultAccount = accounts[0];

            const candidates = await VoterContract.methods.getCandidates(count).call();
            console.log("number of responses: "+candidates.length)

            //const candi = await VoterContract.methods.getCandidates(3).call();
            let candidates2 = [];

            for (let candidate of candidates) {
              if (candidate.id != "0") {
                candidates2.push({
                  candidate_id: candidate.candidate_id,
                  first_name: candidate.first_name,
                  last_name: candidate.last_name,
                  candidate_info: candidate.candidate_info,
                  candidate_desc: candidate.candidate_desc,
                  vote_count: candidate.vote_count
                });
              }
            }
            this.setState({ candidates: candidates2, loading: false });

      };
      handleCandidateChange(event) {
        this.setState({ candidate_id: event.target.value });
      }
      showDetails = (candidate_id) => {
        if (candidate_id) {
          const candidate = this.state.candidates.filter(
            (candidate) => candidate.candidate_id === candidate_id
          );
          this.props.history.push({
            pathname: "/CandidateDetail",
            state: { candidateDetails: candidate },
          });
        }
      };
      async vote() {
        console.log(this.state.candidate_id);
        if (this.state.candidate_id !== 0) {
            var accounts = await web3.eth.getAccounts();
            console.log("web3", accounts);
            console.log("aing: "+ accounts[0]);
            web3.eth.defaultAccount = accounts[0];
            var result = await VoterContract.methods.vote(this.state.candidate_id).send({from: accounts[0]});
            console.log("result"+ JSON.stringify(result[0]));
            if(result[0]== 1){

                swal.fire({
                    icon: 'success',
                    title: "Voting SuccesFull!",
                    text: "You have succesfully voted your candidate",
                    type: "Success",
                    confirmButtonText: "OK"
                  });
            }else{
                swal.fire({
                    icon: 'error',
                    title: 'Voting failed',
                    text: 'Vote from this metamask address has already been casted',
                    confirmButtonText: "OK"
                });
            }
        } else {
            swal.fire({
                icon: 'error',
                title: 'Voting failed',
                text: 'Please Select a Candidate',
                confirmButtonText: "OK"
            });
        }
      }

    render() {
        const { loading, candidates } = this.state;
    let dropDown = null;
    let button = null;
    let ui = null;
    if (loading && candidates.length ==0) {
      ui =   <Loader
      type="ThreeDots"
      color="#00BFFF"
      height={100}
      width={100}
      timeout={3000}
    />;
    }
    if(candidates.length ==0){
      ui = <div>No candidates to load Kindly add candidate info</div>
    }
    if(candidates.length !=0){
      ui = candidates.map((candidate) => {
        return (
          <Candidate
            showDetails={this.showDetails}
            key={candidate.candidate_id}
            candidate={candidate}
          />
        );
      });

      dropDown =  (
        <select style={style} onChange={this.handleCandidateChange}>
          <option>Select Candidate for Vote</option>
          {candidates.map((candidate) => (
            <option key={candidate.candidate_id} value={candidate.candidate_id}>
              {candidate.first_name + " " + candidate.last_name}
            </option>
          ))}
        </select>
      );
        button = (
        <div>
          <button className="btn vote" onClick={this.vote}>
            Vote
          </button>
        </div>
      );

    }

        return ( 
            <div className="container">
                        <div className= "header">
                        <ul className="navbar-nav" id="navg">

                            <li className="nav-item">
                                <a className=" brand">Electronic</a>
                            </li>
                            <li className="nav-item">
                                <a className=" colorb">Ballot</a>
                            </li>
                            <li className="nav-item">
                                <Link to="/welcome" className="nav-link welcome"> Welcome
                            </Link>
                           </li>
                           {/* <li className="nav-item">
                                <Link to="/AdminLogin" className="nav-link user"> {this.props.location.state.username}
                            </Link>
                           </li> */}
                           <li className="nav-item">
                                <Link to="/welcome" className="nav-link user uname"> Vrushali!
                            </Link>
                           </li> 

                        </ul>
                    </div>
                    <div className="row">
        {dropDown}
        {button}
        {ui}
      </div>
      </div>
      ); 
    }
}
  //Export The Main Component
  export default Welcome;
