import React, { Component } from "react";
import renderHTML from 'react-render-html';
import { Link, Redirect } from 'react-router-dom';
import './RegisterInfo.css';
import RegisterStorage from "./stores/RegisterStorage";
import swal from "sweetalert2";
import web3 from './web3';
import ModalToInstallMeta from "./Modal";
import { Button, Modal } from 'react-bootstrap';

const notifier = require('node-notifier');

class RegisterInfo extends Component {
    constructor(props) {
        super(props);
        this.state = {
            password: '',
            fname: '',
            lname: '',
            dob: '',
            gender: '',
            email: '',
            username:'',
            metaMaskInstalled: '',
            show: true,
            showModal: false
        };
        this.onChange = this.onChange.bind(this);
        this.showModal = this.showModal.bind(this);
    }

    componentDidMount(){

    }
    handleClose = () => {
        this.state.show = false;
    }
    handleShow = () => {
        this.state.show = true;
    }
    showtext() {
        var date = document.getElementById("email");
        console.log(date.innerHTML);
        date.type = "date";
        if (date.textContent == "") {
            console.log(date);
            date.type = "text";
        }
        else date.type = "date";
    }

    showModal() {
        console.log(' in modal')
        // this.setState({
        //     showModal: true
        // })
        this.state.showModal = true;
    }

    handleClose() {
        this.state.showModal = false;
    }

    onChange(e) {
        this.setState({ [e.target.name]: e.target.value });
      }

    setInputValue(property, val) {
        val = val.trim();
        if (val.length > 15) {
            return;
        }
        this.setState({
            [property]: val
        })
    }

    pwstrength() {
        var password = document.getElementById("password").value;
        document.getElementById("pwmsg").style.visibility = "visible";
        var bar = document.getElementById("strength");
        var val = bar.value;
        bar.style.visibility = "visible";
        console.log(password.length);
        bar.low = "0.45";
        bar.high = "0.75";
        bar.optimum = "0.8";
        if (password.length < 6) {
            bar.value = "0.3";
            bar.background = "red";
        }
        else if (password.length < 9) bar.value = "0.55";
        else bar.value = "0.8";
    }

    async doRegisterPost() {
        if (!this.state.password)
            return;

        try {
            let res = await fetch('http://localhost:3002/registerpost', {
                method: 'post',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    password: this.state.password,
                    username:this.state.username,
                    fname: this.state.fname,
                    lname: this.state.lname,
                    dob: this.state.dob,
                    gender: this.state.gender,
                    email: this.state.email
                })
            });
            let result = await res.json();
            if (result && result.success) {
                console.log("")
                swal.fire({
                    icon: 'success',
                    title: "Verification Email Sent!",
                    text: "Enter the OTP to verify your Email",
                    type: "Success",
                    confirmButtonText: "OK"
                  });
                console.log("success");
                RegisterStorage.email = this.state.email
                RegisterStorage.username = this.state.username
                this.props.history.push("/verifyemail");

            }

            else if (result && result.success === false) {
                console.log("registration failed ")
                swal.fire({
                    icon: 'error',
                    title: 'Registration failed',
                    text: 'Try Again',
                    confirmButtonText: "OK"
                });
                this.props.history.push("/RegisterInfo");
            }
        }
        catch (e) {

        }
    }

    render() {
        if(typeof web3 === 'undefined')
        {
            console.log("Metamask installed..");
                return (
                    <div className="container">
                        <div className= "header">
                        <ul className="navbar-nav" id="navg">
                            <li className="nav-item">
                            <Link to="/RegisterInfo" className="nav-link reg" id="log"> Register
                            </Link>
                            </li>
                            <li className="nav-item">
                            <Link to="/Login" className="nav-link" id="log"> Login
                            </Link>
                            </li>
                            <li className="nav-item">
                                <a className=" brand">Electronic</a>
                            </li>
                            <li className="nav-item">
                                <a className=" colorb">Ballot</a>
                            </li>
                        </ul>
                    </div>
                    <div className="row">
